import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import logo from '../assets/img/login1.png'; // Import your image

const Login = () => {
  const [formData, setFormData] = useState({
    email: '',
    password: ''
  });

  const { email, password } = formData;

  const onChange = e => setFormData({ ...formData, [e.target.name]: e.target.value });

  const onSubmit = e => {
    e.preventDefault();

    // Dummy login logic (replace with actual login logic)
    console.log("Logged in with email:", email, "and password:", password);

    // Redirect to dashboard page
    window.location.href = '/dashboard';
  };

  return (
    <div className='container mt-5'>
      <div className="text-center">
        <img src={logo} alt="Logo" className="mb-4" /> {/* Include your image */}
        <h1>Sign In</h1>
        <p>Sign into your Account</p>
        <form onSubmit={e => onSubmit(e)}>
          <div className='form-group'>
            <input
              className='form-control'
              type='email'
              placeholder='Email'
              name='email'
              value={email}
              onChange={e => onChange(e)}
              required
            />
          </div>
          <div className='form-group'>
            <input
              className='form-control'
              type='password'
              placeholder='Password'
              name='password'
              value={password}
              onChange={e => onChange(e)}
              minLength='6'
              required
            />
          </div>
          {/* Button type changed to submit */}
          <button className='btn btn-primary' type='submit'>Login</button>
        </form>

        <p className='mt-3'>
          Don't have an account? <Link to='/signup'>Sign Up</Link>
        </p>
        <p className='mt-3'>
          Forgot your Password? <Link to='/reset-password'>Reset Password</Link>
        </p>
      </div>
    </div>
  );
};

export default Login;
