import React from 'react';
import { Link } from 'react-router-dom';

class HomePage extends React.Component {
  render() {
    const containerStyle = {
      display: 'flex',
      flexDirection: 'column',
      justifyContent: 'center',
      alignItems: 'center',
      minHeight: '100vh',
      backgroundImage: `url(${require('../assets/img/header.jpg')})`,
      backgroundSize: 'cover',
      backgroundPosition: 'center',
    };

    return (
      <div style={containerStyle}>
        {/* Render buttons in the middle */}
        <div className="container">
          <div className="row justify-content-center">
            <div className="col-md-3 text-center">
              {/* Use Link component to navigate to Level 1 page */}
              <Link to="/level1">
                <button className="btn btn-primary btn-lg mb-2">Basics of Stock Market</button>
              </Link>
            </div>
            <div className="col-md-3 text-center">
              <Link to="/level2">
                <button className="btn btn-secondary btn-lg mb-2">Fundamental Analysis</button>
              </Link>
            </div>
            <div className="col-md-3 text-center">
              <Link to='/level3'>
                <button className="btn btn-success btn-lg mb-2">Technical Analysis</button>
              </Link>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default HomePage;
