import React, { useEffect } from 'react';

const Activate = (props) => {
  useEffect(() => {
    // Add any necessary side effects here
  }, []);

  return (
    <div style={styles.container}>
      <div style={styles.content}>
        <h1 style={styles.title}>Understanding Stock Markets for Beginners</h1>

        <div style={styles.section}>
          <h2 style={styles.heading}>Introduction to Stock Markets:</h2>
          <p style={styles.paragraph}>
            Stock markets play a pivotal role in the global financial system, providing a platform for buying and selling shares of publicly traded companies. As a beginner, it's crucial to grasp some foundational concepts to navigate this dynamic landscape effectively.
          </p>
        </div>

        <div style={styles.section}>
          <h3 style={styles.subHeading}>Definition:</h3>
          <p style={styles.paragraph}>
            Stock markets, also known as equity markets or share markets, are centralized exchanges where individuals and institutional investors buy and sell ownership stakes (shares) in publicly listed companies.
          </p>
        </div>

        <div style={styles.section}>
          <h3 style={styles.subHeading}>Basic Concepts:</h3>
          <ul style={styles.list}>
            <li><strong>Stocks/Shares:</strong> A stock or share represents ownership in a company. When you own shares, you become a shareholder, entitled to a portion of the company's profits and losses.</li>
            <li><strong>Publicly Listed Companies:</strong> Companies opt to go public by offering shares to the public, and these shares are then listed on stock exchanges. Well-known examples include technology giants like Apple, Google, and Microsoft.</li>
            <li><strong>Stock Exchanges:</strong> Stock exchanges are platforms where buying and selling of stocks take place. Notable ones include the New York Stock Exchange (NYSE) and NASDAQ.</li>
            <li><strong>Bulls and Bears:</strong> In the stock market, you often hear about "bull" and "bear" markets. A bull market is characterized by rising prices and optimistic investor sentiment, while a bear market sees falling prices and pessimistic sentiment.</li>
          </ul>
        </div>

        {/* Add more sections here */}

        <div style={styles.section}>
          <h2 style={styles.heading}>Additional Resources:</h2>
          <p style={styles.paragraph}>
            For additional, more detailed learning, you can refer to <a href="https://zerodha.com/varsity/module/introduction-to-stock-markets/">Zerodha Varsity</a>.
          </p>
          <p style={styles.paragraph}>
            Watch <a href="https://www.youtube.com/watch?v=A7fZp9dwELo">How does the stock market work?</a> on YouTube for a visual explanation.
          </p>
        </div>
      </div>
    </div>
  );
};

const styles = {
  container: {
    
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    minHeight: '100vh',
    background: '#f0f0f0', // Background color
  },
  content: {
    maxWidth: '800px',
    width: '90%',
    padding: '20px',
    background: '#fff', // Content background color
    borderRadius: '10px',
    boxShadow: '0px 0px 10px 0px rgba(0,0,0,0.1)', // Box shadow for container
  },
  title: {
    textAlign: 'center',
    marginBottom: '30px',
    fontSize: '24px',
    fontWeight: 'bold',
  },
  section: {
    marginBottom: '30px',
  },
  heading: {
    fontSize: '20px',
    fontWeight: 'bold',
    marginBottom: '10px',
  },
  subHeading: {
    fontSize: '18px',
    fontWeight: 'bold',
    marginBottom: '10px',
  },
  paragraph: {
    fontSize: '16px',
    lineHeight: '1.6',
    marginBottom: '15px',
  },
  list: {
    fontSize: '16px',
    lineHeight: '1.6',
    paddingLeft: '20px',
    marginBottom: '15px',
  },
};

export default Activate;
